function FA_Currency_Register(){registerFACurrencyEvents()}function registerFACurrencyEvents(){$("input[data-type='currency']").on({keyup:function(){formatCurrency($(this))},blur:function(){formatCurrency($(this),"blur")},onfocusOut:function(){formatCurrency($(this),"fix2")},focusout:function(){formatCurrency($(this),"fix2")}})}function foxCurrencyFloat(e){let r=$("#"+e).val();if(!(r.indexOf(",")>=0))return getFoxAsomeNumbers(r);{let n=r.substring(r.indexOf(",")+1,r.length);return getFoxAsomeNumbers(r.substring(0,r.indexOf(",")))+"."+n}}function formatNumber(e){return e.replace(/\D/g,"").replace(/\B(?=(\d{3})+(?!\d))/g,".")}function getFoxAsomeNumbers(e){return e.replace(/\D/g,"").replace(/\B(?=(\d{3})+(?!\d))/g,"")}function formatCurrency(e,r){let n=e.val();if(""===n)return;let t=n;if(n.indexOf(",")>=0){if(n.indexOf(",")===n.length-1)return;let u=n.substring(n.indexOf(",")+1,n.length);t=formatNumber(n.substring(0,n.indexOf(",")))+","+u}else t=formatNumber(n);"fix2"===r&&t.length>0?0>=n.indexOf(",")&&e.val("R$ "+t+",00"):e.val("R$ "+t);let i=n.length,f=e.prop("selectionStart");f=n.length-i+f,e[0].setSelectionRange(f,f)}$(document).ready(function(){registerFACurrencyEvents()});